import React, { useState, useRef } from 'react';
import MovieList from './components/MovieList';
import './App.css';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isRetrying, setIsRetrying] = useState(false);
  const retryTimeoutRef = useRef();

  async function fetchMoviesHandler() {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('https://swapi.dev/api/films/');
      if (!response.ok) {
        throw new Error('Failed to fetch');
      }

      const data = await response.json();
      console.log('API Data:', data);

      if (!data.results || data.results.length === 0) {
        throw new Error('No movies found in response');
      }

      const transformedMovies = data.results.map(movieData => ({
        id: movieData.episode_id,
        title: movieData.title,
        openingText: movieData.opening_crawl,
        releaseDate: movieData.release_date
      }));

      setMovies(transformedMovies);
      setIsRetrying(false);
    } catch (error) {
      console.error('Fetch error:', error);
      setMovies([]);
      setError('Something went wrong... Retrying');
      setIsRetrying(true);
      retryTimeoutRef.current = setTimeout(fetchMoviesHandler, 5000);
    } finally {
      setIsLoading(false);
    }
  }

  function cancelRetryHandler() {
    clearTimeout(retryTimeoutRef.current);
    setIsRetrying(false);
    setError('Retry cancelled.');
  }

  return (
    <div className="app-container">
      <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      {isRetrying && <button onClick={cancelRetryHandler}>Cancel Retry</button>}
      {isLoading && <div className="loader"></div>}
      {!isLoading && movies.length > 0 && <MovieList movies={movies} />}
      {!isLoading && movies.length === 0 && error && <p className="no-movies">{error}</p>}
      {!isLoading && movies.length === 0 && !error && (
        <p className="no-movies">No movies found. Try again.</p>
      )}
    </div>
  );
};

export default App;
