import React, { useState, useEffect, useCallback, useRef } from 'react';
import MoviesList from './components/MoviesList';
import './App.css';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isRetrying, setIsRetrying] = useState(false);

  const [title, setTitle] = useState('');
  const [openingText, setOpeningText] = useState('');
  const [releaseDate, setReleaseDate] = useState('');

  const retryTimeoutRef = useRef(null);

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('https://swapi.py4e.com/api/films/');
      if (!response.ok) {
        throw new Error('Failed to fetch');
      }

      const data = await response.json();
      if (!data.results) {
        throw new Error('No data received');
      }

      const transformedMovies = data.results.map((movieData) => ({
        id: movieData.episode_id,
        title: movieData.title,
        openingText: movieData.opening_crawl,
        releaseDate: movieData.release_date,
      }));

      setMovies(transformedMovies);
      setIsRetrying(false);
    } catch (err) {
      console.error('Fetch error:', err);
      setError('Something went wrong... Retrying');
      setIsRetrying(true);
      retryTimeoutRef.current = setTimeout(fetchMoviesHandler, 5000);
    } finally {
      setIsLoading(false);
    }
  }, []);

  const handleFetchClick = () => {
    clearTimeout(retryTimeoutRef.current);
    setIsRetrying(false);
    fetchMoviesHandler();
  };

  const handleCancelRetry = () => {
    clearTimeout(retryTimeoutRef.current);
    setIsRetrying(false);
    setError('Retry cancelled.');
  };

  const addMovieHandler = useCallback((event) => {
    event.preventDefault();

    const newMovie = {
      title,
      openingText,
      releaseDate,
    };

    console.log(newMovie);

    // Clear form fields after submit
    setTitle('');
    setOpeningText('');
    setReleaseDate('');
  }, [title, openingText, releaseDate]);

  useEffect(() => {
    fetchMoviesHandler();
    return () => {
      clearTimeout(retryTimeoutRef.current);
    };
  }, [fetchMoviesHandler]);

  return (
    <div className="app-container">
      <form onSubmit={addMovieHandler} className="add-movie-form">
        <div>
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Opening Text</label>
          <textarea
            value={openingText}
            onChange={(e) => setOpeningText(e.target.value)}
            required
          ></textarea>
        </div>
        <div>
          <label>Release Date</label>
          <input
            type="date"
            value={releaseDate}
            onChange={(e) => setReleaseDate(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="add-movie-button">
          Add Movie
        </button>
      </form>

      <button onClick={handleFetchClick} disabled={isRetrying}>
        {isRetrying ? 'Retrying...' : 'Fetch Movies'}
      </button>
      {isRetrying && (
        <button onClick={handleCancelRetry} className="cancel-button">
          Cancel Retry
        </button>
      )}

      {isLoading && <div className="loader"></div>}
      {!isLoading && error && <p className="no-movies">{error}</p>}
      {!isLoading && !error && movies.length > 0 && <MoviesList movies={movies} />}
      {!isLoading && !error && movies.length === 0 && (
        <p className="no-movies">No movies to display</p>
      )}
    </div>
  );
};

export default App;
