import React, { useState, useEffect } from 'react';
import MoviesList from './components/MovieList';
import './App.css';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [retryCount, setRetryCount] = useState(0);
  const [isRetrying, setIsRetrying] = useState(false);

  const fetchMovies = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('https://swapi.dev/api/films/');
      if (!response.ok) throw new Error('Failed to fetch');

      const data = await response.json();
      if (!data.results) throw new Error('No data received');

      const transformedMovies = data.results.map(movieData => ({
        id: movieData.episode_id,
        title: movieData.title,
        openingText: movieData.opening_crawl,
        releaseDate: movieData.release_date
      }));

      setMovies(transformedMovies);
      setIsRetrying(false);
    } catch (err) {
      setError(err.message);
      if (isRetrying) {
        setRetryCount(prev => prev + 1);
      }
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (isRetrying) {
      const timer = setTimeout(fetchMovies, 5000);
      return () => clearTimeout(timer);
    }
  }, [isRetrying, retryCount]);

  const handleFetchClick = () => {
    setIsRetrying(true);
    fetchMovies();
  };

  const handleCancelRetry = () => {
    setIsRetrying(false);
    setRetryCount(0);
  };

  return (
    <div className="app-container">
      <button onClick={handleFetchClick} disabled={isRetrying}>
        {isRetrying ? 'Fetching Movies...' : 'Fetch Movies'}
      </button>

      {isRetrying && (
        <button onClick={handleCancelRetry} className="cancel-button">
          Cancel Retry
        </button>
      )}

      {isLoading ? (
        <div className="loader"></div>
      ) : error ? (
        <div className="error-message">
          <p>Something went wrong... Retrying ({retryCount})</p>
          {!isRetrying && (
            <button onClick={handleFetchClick} className="retry-button">
              Try Again
            </button>
          )}
        </div>
      ) : movies.length > 0 ? (
        <MovieList movies={movies} />
      ) : (
        <p className="no-movies">No movies to display</p>
      )}
    </div>
  );
};

export default App;
